               eieb.attribute_char1 hsn_or_sac
          FROM egp_system_items_tl esit,
               egp_system_items_b esib,
               ego_item_eff_b eieb,
               inv_org_parameters iop
         WHERE     1 = 1
               AND iop.organization_id=esit.organization_id
