                              NULL
                      END                                           payment_method
                      ,CASE
                          WHEN rctl.INTERFACE_LINE_CONTEXT IN ('CBS', 'CCCP', 'CPA', 'CPP', 'KIOSK', 'MSA/POS', 'PBSS', 'ValYou')
                            THEN rctl.INTERFACE_LINE_ATTRIBUTE1
