       SR.segment_map_type SEGMENT_MAP_TYPE, 
       decode(SR.segment_map_type,'C',SS.segment_code,'R',SS.segment_code,'')    SOURCE_SEGMENT_NAME,
       decode(SR.segment_map_type, 'S', SR.single_value) TARGET_SEGMENT_VALUE,                 
