  ,POL.LINE_NUM "PO LINE NUM"
  ,DECODE (RCV.TRANSACTION_TYPE, 'RECEIVE', 'RECEIVED'
								,'DELIVER', 'DELIVERED', NULL) "RECEIPT STATUS"
