   group by inventory_item_id
   ),
Overhead AS (select DECODE(MAX(cost_method_code), 'STANDARD',SUM(unit_cost),MAX(unit_cost)) UC2,
   inventory_item_id
   FROM
   cst_costed_vu_costs_v A1,
