        J.reference_7                           reference7,
        J.reference_8                           reference8,
        J.reference_9                           reference9,
        J.reference_10                          reference10,
        nvl(J.stat_amount,0)                    Line_Stat_Amount,
        &ACCOUNT_FLEX                           FLEXDATA_DSP
