            LEFT OUTER JOIN
                POZ_suppliers_v PV ON rtxn.vendor_id = PV.vendor_id
				
			LEFT OUTER JOIN 
				rcv_shipment_lines RSL ON RTXN.shipment_line_id = RSL.shipment_line_id
			LEFT OUTER JOIN 
				rcv_shipment_headers RSH ON RSL.shipment_header_id = RSH.shipment_header_id
			LEFT OUTER JOIN 
