--and aid.org_id=300000001564818
  AND ai.invoice_type_lookup_code NOT IN ('INTEREST')
  AND NVL(ai.intercompany_flag,'N')    = DECODE('Y','E','N','O','Y',NVL(ai.intercompany_flag,'N'))
  GROUP BY ai.invoice_id
