              FROM gl_balances gb,
                   gl_code_combinations gcc,
                   gl_ledgers gl,
                   gl_periods gp,
                   gl_periods gp1,
                   (SELECT ffvv.description location_desc, ffvv.flex_value location_segment, attribute2 location_gstin
