      AND OrgContactRole.ORG_CONTACT_ID(+) = OrgContact.ORG_CONTACT_ID
      and OrgContactRole.ROLE_TYPE(+) = 'ADMINISTRATIVE_CONTACT_FOR'
      and OrgContactRole.STATUS(+) = 'A'
