		-- AND ( TO_DATE(TO_CHAR(RCTA.TRX_DATE, 'DD-MM-YYYY'), 'DD-MM-YYYY') BETWEEN TO_DATE(TO_CHAR(:P_FROM_DATE, 'DD-MM-YYYY'), 'DD-MM-YYYY') AND TO_DATE(TO_CHAR(:P_TO_DATE, 'DD-MM-YYYY'), 'DD-MM-YYYY') )
